function iconnavs_Font_Icons( $item_id='' ) {
	global $ficons;
	$select = '';
	foreach( $ficons as $ic ) {
		$select .= '<span class="icon_cell" name="'. $item_id .'"><em class="'.$ic.'" title="'. ucfirst( str_replace('icon-', '', $ic) ).'"></em></span>';
  	}
  	$select .= '<button type="image" class="font_icon_use butt button" name="font_icon" id="'.$item_id.'">Use icon</button>';
  	return $select;
}


function iconnavs_Font_Icons_Jsless( $item_id='', $icons =''  ) {
	global $ficons;
	foreach( $ficons as $ic ) {
		$link .= '<span class="icon_cell"><a href="'. esc_url(
						add_query_arg(
							array(
								'selected_font_icon' => $ic
							))							
					). '"><em class="'.$ic.'"></em></a></span>';
	} return $link;
}
return $ficons;

?>
